import React from "react";
import { StyleSheet, View, Text, TouchableOpacity } from "react-native";
import { Avatar } from "./Avatar";
import { SmallLinkPreview } from "./SmallLinkPreview";
import { MaterialCommunityIcons } from "@expo/vector-icons";
import { useNavigation } from "@react-navigation/native";
import { useTheme } from "@react-navigation/native";

function ThreadRow({ item, onPress }) {
  const navigation = useNavigation();
  const { colors } = useTheme();

  return (
    <TouchableOpacity
      onPress={() => navigation.navigate("Thread", { itemId: item.id })}
    >
      <View
        style={[
          styles.container,
          { backgroundColor: colors.card, borderColor: colors.card },
        ]}
      >
        <View style={styles.topArea}>
          <Avatar avatarName={item.user} size={26}></Avatar>
          <Text style={styles.userText}>
            Posted by {item.user} - {item.time}
          </Text>
        </View>
        <View style={styles.middleArea}>
          <Text style={[styles.middleText, { color: colors.text }]}>
            {item.title}
          </Text>
          {item.title == "Not only did we score but" ? (
            <SmallLinkPreview url="https://www.bbc.com/sport/football/55321152" />
          ) : //<SmallLinkPreview url="https://www.evertonfc.com/news/1939598/evertons-january-premier-league-fixtures-moved-for-tv" />
          null}
        </View>
        <View style={styles.bottomArea}>
          <MaterialCommunityIcons name="comment" size={18} color="#949494" />
          <Text style={styles.bottomText}>{item.comments}</Text>
          <MaterialCommunityIcons
            name="calendar-blank"
            size={18}
            color="#949494"
          />
          <Text style={styles.bottomText}>{item.date}</Text>
        </View>
      </View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginHorizontal: 10,
    marginTop: 10,
    padding: 2,
    borderRadius: 4,
    borderWidth: 1,
  },
  middleText: {
    fontWeight: "bold",
    flex: 1,
    fontSize: 22,
    padding: 10,
  },
  userText: {
    color: "#949494", //THEME CHANGES
    fontStyle: "italic",
    fontSize: 13,
    paddingTop: 10,
    paddingBottom: 10,
    paddingLeft: 5,
  },
  bottomText: {
    color: "#949494", //THEME CHANGES
    fontWeight: "600",
    textAlign: "center",
    textAlignVertical: "center",
    fontSize: 15,
    paddingLeft: 5,
    paddingRight: 25,
  },
  topArea: {
    flex: 1,
    flexDirection: "row",
    paddingLeft: 10,
    alignItems: "center",
  },
  middleArea: {
    flex: 1,
    flexDirection: "row",
  },
  bottomArea: {
    flex: 1,
    flexDirection: "row",
    paddingLeft: 10,
    alignItems: "center",
    paddingBottom: 6,
  },
});

export default ThreadRow;
